import React, {Component, useEffect, useState} from 'react';
import { withNamespaces } from 'react-i18next';
import {
    loadModels,
    getFullFaceDescription,
    createMatcher,
    isFaceDetectionModelLoaded
} from '../../components/api/face';
import DrawBox from '../../components/api/DrawBox';
import ShowDescriptors from '../../components/api/showDescriptors';
import * as JSON_PROFILE from '../../components/api/descriptor.json';
import testImg from '../../components/api/img/test.png';




const MaxWidth = 600;
const boxColor = '#BE80B5';

const INIT_STATE = {
    url: '',
    imageURL: '',
    fullDesc: [],
    imageDimension: {
        width: 300,
        height: 100
    },
    error: false,
    loading: false
};

const Register = () =>{
    const [state, setState] = useState({
        ...INIT_STATE,
        faceMatcher: null,
        showDescriptors: false,
        WIDTH: 300,
        HEIGHT: 100,
        isModelLoaded: !!isFaceDetectionModelLoaded()
    });

    const mounting = async () => {
        await loadModels();
        await matcher();
        await getImageDimension(testImg);
        await setState({ imageURL: testImg, loading: true });
        await handleImageChange(testImg);
    };

    const matcher = async () => {
        const faceMatcher = await createMatcher(JSON_PROFILE);
        setState({ ...state, faceMatcher });
    };

    const handleFileChange = async event => {
        resetState();
        await this.setState({
            imageURL: URL.createObjectURL(event.target.files[0]),
            loading: true
        });
        handleImageChange();
    };

    const handleURLChange = event => {
        setState({ ...state, url: event.target.value });
    };

    const handleButtonClick = async () => {
        resetState();
        let blob = await fetch(state.url)
            .then(r => r.blob())
            .catch(error => setState({ ...state, error }));
        if (!!blob && blob.type.includes('image')) {
            setState({...state,
                imageURL: URL.createObjectURL(blob),
                loading: true
            });
            handleImageChange();
        }
    };

    const handleImageChange = async (image = state.imageURL) => {
        await getImageDimension(image);
        await getFullFaceDescription(image).then(fullDesc => {
            console.log('============>',typeof(fullDesc));
            console.log('============>',fullDesc);
            setState({ ...state, fullDesc, loading: false });
        });
    };

    const getImageDimension = imageURL => {
        let img = new Image();
        img.onload = () => {
            let HEIGHT = (state.WIDTH * img.height) / img.width;
            setState({...state,
                HEIGHT,
                imageDimension: {
                    width: img.width,
                    height: img.height
                }
            });
        };
        img.src = imageURL;
    };

    const handleDescriptorsCheck = event => {
        setState({ ...state, showDescriptors: event.target.checked });
    };

    const resetState = () => {
       setState({ ...state, ...INIT_STATE });
    };

    useEffect(()=>{
        // componentWillMount
        resetState();
        let _W = document.documentElement.clientWidth;
        if (_W > MaxWidth) _W = MaxWidth;
        setState({ ...state, WIDTH: _W });
        mounting().then(null);

    }, [])


    const  {
        url,
        WIDTH,
        HEIGHT,
        imageURL,
        fullDesc,
        faceMatcher,
        showDescriptors,
        isModelLoaded,
        error,
        loading
    } = state;


        // Display working status
        let status = <p>Status: Model Loaded = {isModelLoaded.toString()}</p>;
        if (!!error && error.toString() === 'TypeError: Failed to fetch') {
            status = (
                <p style={{ color: 'red' }}>Status: Error Failed to fetch Image URL</p>
            );
        } else if (loading) {
            status = <p style={{ color: 'blue' }}>Status: LOADING...</p>;
        } else if (!!fullDesc && !!imageURL && !loading) {
            if (fullDesc.length < 2)
                status = <p>Status: {fullDesc.length} Face Detect</p>;
            if (fullDesc.length > 1)
                status = <p>Status: {fullDesc.length} Faces Detect</p>;
        }

        // Loading Spinner
        let spinner = (
            <div
                style={{
                    margin: 0,
                    color: '#BE80B5',
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%,-50%)',
                    textShadow: '2px 2px 3px #fff'
                }}
            >
                <div className="loading" />
                <h3>Processing...</h3>
            </div>
        );

        return (
            <div
                style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center'
                }}
            >
                {status}
                <div
                    style={{
                        position: 'relative',
                        width: WIDTH,
                        height: HEIGHT
                    }}
                >
                    {!!imageURL ? (
                        <div
                            style={{
                                position: 'relative'
                            }}
                        >
                            <div style={{ position: 'absolute' }}>
                                <img style={{ width: WIDTH }} src={imageURL} alt="imageURL" />
                            </div>
                            {!!fullDesc ? (
                                <DrawBox
                                    fullDesc={fullDesc}
                                    faceMatcher={faceMatcher}
                                    imageWidth={WIDTH}
                                    boxColor={boxColor}
                                />
                            ) : null}
                        </div>
                    ) : null}
                    {loading ? spinner : null}
                </div>
                <div
                    style={{
                        width: WIDTH,
                        padding: 10,
                        border: 'solid',
                        marginTop: 10
                    }}
                >
                    <p>VERIFICA TU IDENTIDAD</p>
                    <input
                        id="myFileUpload"
                        type="file"
                        onChange={(e)=>handleFileChange(e)}
                        accept=".jpg, .jpeg, .png"
                    />
                    <br />
                    {/*  <div className="URLInput">
            <input
              type="url"
              name="url"
              id="url"
              placeholder="Place your photo URL here (only .jpg, .jpeg, .png)"
              pattern="https://.*"
              size="30"
              onChange={this.handleURLChange}
            />
            <button onClick={this.handleButtonClick}>Upload</button>
          </div>*/}
                    <div>
                        <input
                            name="descriptors"
                            type="checkbox"
                            checked={showDescriptors}
                            onChange={(e)=>handleDescriptorsCheck(e)}
                        />
                        <label>Show Descriptors</label>
                    </div>
                    {!!showDescriptors ? <ShowDescriptors fullDesc={fullDesc} /> : null}
                </div>
            </div>
        );
}


export default withNamespaces()(Register);
