import React, {Component, Fragment, useEffect, useState} from 'react';
import { withNamespaces } from 'react-i18next';
import {
    loadModels,
    getFullFaceDescription,
    createMatcher,
    isFaceDetectionModelLoaded
} from '../../components/api/face';
import DrawBox from '../../components/api/DrawBox';
import ShowDescriptors from '../../components/api/showDescriptors';
import * as JSON_PROFILE from '../../components/api/descriptor.json';
import testImg from '../../components/api/img/test.png';




const MaxWidth = 600;
const boxColor = '#BE80B5';

const INIT_STATE = {
    url: '',
    imageURL: '',
    fullDesc: [],
    imageDimension: {
        width: 300,
        height: 100
    },
    error: false,
    loading: false
};

const Register = ({t}: any)=>{
    const [avatarBase64, setAvatarBase64] = useState('')
    const [state, setState] = useState({
        ...INIT_STATE,
        faceMatcher: null,
        showDescriptors: false,
        WIDTH: 300,
        HEIGHT: 100,
        isModelLoaded: !!isFaceDetectionModelLoaded()
    });


    const resetState = () => {
        setState({ ...state, ...INIT_STATE });
    };

    const matcher = async () => {
        const faceMatcher = await createMatcher(JSON_PROFILE);
        // console.log('=>>>>>>>>>>>>>>', typeof(faceMatcher))
        //setState({ ...state, faceMatcher });
    };

    const mounting = async () => {
        await loadModels();
        await matcher();
        await getImageDimension(testImg);
        await setState({...state,  imageURL: testImg, loading: true });
        await handleImageChange(testImg);
    };

    useEffect(()=>{
        // componentWillMount
        resetState();
        let _W = document.documentElement.clientWidth;
        if (_W > MaxWidth) _W = MaxWidth;
        setState({ ...state, WIDTH: _W });
        mounting().then(null);

    }, [])

    const  {
        url,
        WIDTH,
        HEIGHT,
        imageURL,
        fullDesc,
        faceMatcher,
        showDescriptors,
        isModelLoaded,
        error,
        loading
    } = state;







    const encodeFileBase64 = (file: any) => {
        let reader = new FileReader();
        if (file) {
            reader.readAsDataURL(file);
            reader.onload = () => {
                let Base64 = reader.result as string;
                console.log('This is Base64  --->', `${Base64}`);
                setAvatarBase64(Base64);
            };
            reader.onerror = (error) => {
                console.log("error: ", error);
            };
        }
    };
        const createPreviewImage = (file: any) => URL.createObjectURL(file);

        const handleFileChange = async (event: any) => {
            resetState();
            const inputValue = event.target.files[0];
            encodeFileBase64(inputValue)
            // await
            setState({...state,
                imageURL: createPreviewImage(inputValue),
                loading: true
            });
             await handleImageChange();
        };

        const handleURLChange = (event: any) => {
            setState({ ...state, url: event.target.value });
        };



        const handleImageChange = async (image = imageURL) => {
            await getImageDimension(image);
            await getFullFaceDescription(image).then(fullDesc => {
               // @ts-ignore
                setState({ ...state, loading: false, fullDesc });
            });
        };

        const getImageDimension = (imageURL: any) => {
            let img = new Image();
            img.onload = () => {
                let HEIGHT = (WIDTH * img.height) / img.width;
                setState({...state,
                    HEIGHT,
                    imageDimension: {
                        width: img.width,
                        height: img.height
                    }
                });
            };
            img.src = imageURL;
        };

        const handleDescriptorsCheck = (e: any) => {
            setState({ ...state, showDescriptors: e.target.value });
        };


            // Display working status
            let status = <p>Status: Model Loaded = {isModelLoaded.toString()}</p>;
            if (error && error.toString() === 'TypeError: Failed to fetch') {
                status = (
                    <p style={{ color: 'red' }}>Status: Error Failed to fetch Image URL</p>
                );
            } else if (loading) {
                status = <p style={{ color: 'blue' }}>Status: LOADING...</p>;
            } else if (!!fullDesc && !!imageURL && !loading) {
                if (fullDesc.length < 2)
                    status = <p>Status: {fullDesc.length} Face Detect</p>;
                if (fullDesc.length > 1)
                    status = <p>Status: {fullDesc.length} Faces Detect</p>;
            }

            // Loading Spinner
            const spinner = (
                <div
                    style={{
                        margin: 0,
                        color: '#BE80B5',
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%,-50%)',
                        textShadow: '2px 2px 3px #fff'
                    }}
                >
                    <div className="loading" />
                    <h3>Processing...</h3>
                </div>
            );

            return (
                <div
                    style={{
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'center'
                    }}
                >
                    {status}
                    <div
                        style={{
                            position: 'relative',
                            width: WIDTH,
                            height: HEIGHT
                        }}
                    >
                        {/*{!!imageURL ? (
                            <div
                                style={{
                                    position: 'relative'
                                }}
                            >
                                <div style={{ position: 'absolute' }}>
                                    <img style={{ width: WIDTH }} src={imageURL} alt="imageURL" />
                                </div>
                                {!!fullDesc ? (
                                    <DrawBox
                                        fullDesc={fullDesc}
                                        faceMatcher={faceMatcher}
                                        imageWidth={WIDTH}
                                        boxColor={boxColor}
                                    />
                                ) : null}
                            </div>
                        ) : null}*/}
                        {loading ? spinner : null}
                    </div>
                    <div
                        style={{
                            width: WIDTH,
                            padding: 10,
                            border: 'solid',
                            marginTop: 10
                        }}
                    >
                        <p>VERIFICA TU IDENTIDAD</p>
                        <input
                            id="myFileUpload"
                            type="file"
                            onChange={(e)=>handleFileChange(e)}
                            accept=".jpg, .jpeg, .png"
                        />


                        <div>
                            <input
                                name="descriptors"
                                type="checkbox"
                                checked={showDescriptors}
                                onChange={(e)=>handleDescriptorsCheck(e)}
                            />
                            <label>Show Descriptors</label>
                        </div>
                        {showDescriptors ? <ShowDescriptors fullDesc={fullDesc} /> : null}
                    </div>
                </div>
            );

    }


export default withNamespaces()(Register);
