import React, {Fragment, useContext} from 'react';
import '@lottiefiles/lottie-player';

import { withNamespaces } from 'react-i18next';
import FaceIcon from '@material-ui/icons/Face';
import LockIcon from '@material-ui/icons/Lock';
import {Link} from "react-router-dom";

import { SocketContext } from '../../context/SocketContext'
import {LoginScreen} from "../auth/LoginScreen";

const FormComponent =({t}) => {

    const { socket } = useContext( SocketContext );

    const handleConnectSocket = () =>{
        socket.emit( 'crear-banda', { nombre: "Erick" });
    }

    return  <Fragment>
        <div className="form">

            <div className="sh_form">
                <lottie-player src="/lottie/person.json"  background="transparent"  speed="1"  style={{width: '300px', height: '300px', display: 'flex', margin: 'auto', justifyContent: 'center'}}loop  autoplay/>

                
                <div className="sh_block sh__mt2">
                    <LoginScreen/>
                </div>
            </div>
        </div>
    </Fragment>
}

export default withNamespaces()(FormComponent);
